timeLimits = [2.267574e-05 1.5]; % seconds
frequencyLimits = [0 22050]; % Hz
overlapPercent = 50;

%%
% Index into signal time region of interest
x01_ROI = x01(:);
timeValues = a;
minIdx = timeValues >= timeLimits(1);
